import React, { Component } from 'react';
import axios from 'axios';
import './login.css'
//
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../../actions/authActions";
import classnames from "classnames";
class Login extends Component {
    constructor(props) {
        super(props);
        this.onChangemail = this.onChangemail.bind(this);
        this.onChangepass = this.onChangepass.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
          email: '',
          password: '',
          //ampu
          errors: {}
        }
      }

      //ti
      componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
          this.setState({
            errors: nextProps.errors
          });
        }

      }
      onChangemail(e) {
        this.setState({
          email: e.target.value
        });
      }
      onChangepass(e) {
        this.setState({
          password: e.target.value
        })  
      }
      onSubmit(e) {
        e.preventDefault();
        const obj = {
          email: this.state.email,
          password: this.state.password
        };
        axios.post('http://localhost:8080/login', obj)
            .then(res => console.log(res.data));

            //alert(res.data)
        this.setState({
          email: '',
          password: ''
        })
      }
    render() {
        return (

            <div class="wrapper fadeInDown">
            <div id="formContent">
              <div class="fadeIn first">

              </div>

              <form onSubmit={this.onSubmit}>
                <input type="text" id="login" class="fadeIn second"  placeholder="e-mail"
                value={this.state.email}

                //eto
                className={classnames("", {
                  invalid: errors.email || errors.emailnotfound
                })}

                onChange={this.onChangemail}/>


<span className="red-text">
                  {errors.email}
                  {errors.emailnotfound}
                </span>


                <input type="text" id="password" class="fadeIn third"  placeholder="password"
                value={this.state.password}
            //ti ko
                className={classnames("", {
                  invalid: errors.password || errors.passwordincorrect
                })}


                onChange={this.onChangepass}/>



<span className="red-text">
                  {errors.password}
                  {errors.passwordincorrect}
                </span>



                <input type="submit" class="fadeIn fourth" value="Log In"/>
              </form>

              <div id="formFooter">
                <a class="underlineHover" href="ff">Forgot Password?</a>
              </div>

            </div>
          </div>
        );
    }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});
export default connect(
  mapStateToProps,
  { loginUser }
)(Login);
