import React, { Component } from 'react';
import axios from 'axios';


export default class ListTous extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/profil')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
            <div className="table-responsive">

                        {
                            (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {
                                return (

<div class="card">


<img class="card-img-top"  src={'http://localhost:8080/user/'+obj.photo_profil}  alt="Card image cap"/>


<div class="card-body">


  <h4 class="card-title">{obj.nom}</h4>

  <p class="card-text">{obj.email}</p>
  <p class="card-text">{obj.password}</p>
  <a href="gg" class="btn btn-primary">Button</a>

</div>

</div>)

                            })) : ('')
                        }

        </div>
        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
